import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  username: string;

  constructor() {
    // Pobierz nazwę użytkownika zalogowanego użytkownika (np. z kontekstu uwierzytelniania) i przypisz do właściwości username
    this.username = 'John Doe';
  }
}
<div class="welcome">
  <h1>Witaj, {{ username }}!</h1>
  <p>Witamy na naszej stronie powitalnej.</p>
</div>
