import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

interface Message {
  username: string;
  content: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  username: string;
  messageForm: FormGroup;
  messages: Message[] = [];
  isAuthenticated: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient,
    private router: Router
  ) {
    this.username = 'John Doe'; // Nazwa zalogowanego użytkownika
    this.messageForm = this.formBuilder.group({
      content: ['', Validators.required]
    });
  }

  ngOnInit() {
    this.isAuthenticated = this.checkAuthentication();
    if (this.isAuthenticated) {
      this.fetchMessages();
    } else {
      this.router.navigate(['/login']); // Przekierowanie do komponentu logowania
    }
  }

  checkAuthentication(): boolean {
    const token = localStorage.getItem('token'); // Pobierz token z localStorage

    if (token) {
      // Sprawdź, czy token jest ważny (możesz wykonać dodatkowe sprawdzenia, np. sprawdzić jego ważność lub poprawność)

      return true; // Użytkownik jest zalogowany
    }

    return false; // Użytkownik nie jest zalogowany
  }

  fetchMessages() {
    // Pobierz wiadomości z serwera i przypisz do tablicy messages
    this.http.get<Message[]>('http://localhost:3000/api/messages')
      .subscribe(
        response => {
          this.messages = response;
        },
        error => {
          console.log('Błąd podczas pobierania wiadomości:', error);
        }
      );
  }

  submitMessage() {
    if (this.messageForm.invalid) {
      return;
    }

    if (!this.isAuthenticated) {
      this.router.navigate(['/login']); // Przekierowanie do komponentu logowania
      return;
    }

    const content = this.messageForm.controls['content'].value;

    // Wysłanie wiadomości do serwera
    this.http.post('http://localhost:3000/api/messages', { username: this.username, content })
      .subscribe(
        response => {
          console.log('Wiadomość wysłana');
          this.messageForm.reset();
          this.fetchMessages(); // Odświeżenie wiadomości po wysłaniu
        },
        error => {
          console.log('Błąd podczas wysyłania wiadomości:', error);
        }
      );
  }

  logout() {
    localStorage.removeItem('token'); // Usunięcie tokenu z localStorage
    this.isAuthenticated = false;
    this.router.navigate(['/login']); // Przekierowanie do komponentu logowania
  }
}
<h2>Witaj, {{ username }}!</h2>

<!-- Formularz do wprowadzania wiadomości -->
<form [formGroup]="messageForm" (ngSubmit)="submitMessage()">
  <textarea formControlName="content" rows="4" placeholder="Wpisz swoją wiadomość"></textarea>
  <button type="submit" [disabled]="messageForm.invalid">Wyślij</button>
</form>

<!-- Lista wiadomości -->
<div *ngIf="messages.length > 0">
  <h3>Wiadomości użytkowników:</h3>
  <ul>
    <li *ngFor="let message of messages">
      <span class="username">{{ message.username }}:</span>
      <span class="content">{{ message.content }}</span>
    </li>
  </ul>
</div>
<div *ngIf="messages.length === 0">
  <p>Brak wiadomości do wyświetlenia.</p>
</div>
