// login.component.ts
import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private router: Router) {}

  login() {
    // Logika logowania użytkownika
    // Przykładowo, sprawdzanie poprawności loginu i hasła

    if (this.username === 'admin' && this.password === 'admin') {
      // Jeśli dane logowania są poprawne, przekieruj do komponentu Home
      this.router.navigate(['/home']);
    } else {
      // Jeśli dane logowania są niepoprawne, wyświetl informację o błędzie
      alert('Nieprawidłowy login lub hasło!');
    }
  }

  goToRegister() {
    // Przekieruj do komponentu Rejestracja
    this.router.navigate(['/register']);
  }
}
<!-- login.component.html -->
<div class="login-container">
  <h2>Logowanie</h2>
  <form (ngSubmit)="login()">
    <div class="form-group">
      <label for="username">Login:</label>
      <input type="text" id="username" name="username" [(ngModel)]="username" required>
    </div>
    <div class="form-group">
      <label for="password">Hasło:</label>
      <input type="password" id="password" name="password" [(ngModel)]="password" required>
    </div>
    <button type="submit" class="btn btn-primary">Zaloguj</button>
    <button class="btn btn-link" (click)="goToRegister()">Rejestracja</button>
  </form>
</div>
